package BitManipulation;

public class Oppisitive_Num {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = -2;
		int b = -3;
		int b1 = a&1;
		int b2 = b&1;
		if(b1 == b2) {
			System.out.println("Same");
		}else {
			System.out.println("opposite");
		}

	}

}
